import axios from "axios";
import React, { useState, useEffect } from "react";
import { View, Text, StyleSheet, Image, ImageBackground } from "react-native";
import { SafeAreaView } from "react-navigation";
import SavedMovieForm from "../components/SavedMovieForm";
const url = "http://www.omdbapi.com/?apikey=a091d52e";
const image = {
  uri: "https://c0.wallpaperflare.com/preview/902/404/8/blurred-background-close-up-colors-dark.jpg",
};

const SavedScreen = ({ navigation }) => {
  const id = navigation.getParam("id");
  const [savedMovies] = useState([]);
  const [result, setResult] = useState(null);
  const getResult = async () => {
    const response = await axios.get(`${url}&i=${id}`);
    setResult(response.data);
  };
  useEffect(() => {
    getResult(id);
  }, []);
  if (!result) {
    return null;
  }

  return (
    <SafeAreaView>
      <ImageBackground
        source={{ uri: result.Poster }}
        blurRadius={3}
        resizeMode="cover">
        <View style={styles.background}>
        {/* <FlatList 
              data= {savedMovies}
              keyExtrator = {blogPost => blogPost.title}
              renderItem = {( {item }) =>{
                return item
              }}
            /> */}
        <View style={styles.container}>
          <Text style={styles.title}>{result.Title}</Text>
          
          <Image
            style={styles.image}
            source={{ uri: result.Poster }}
            resizeMode="contain"
          />
          <Text style={styles.plot}>Plot: {result.Plot}</Text>
          <Text style={styles.plot}>Language: {result.Language}</Text>
          <SavedMovieForm/>
        </View> 
        </View>
      </ImageBackground>
      </SafeAreaView>
  );
};
const styles = StyleSheet.create({
  background: {
    color: '#fff',
    height: '100%'
  },
  image: {
    marginTop: 20,
    // width: "100%",
    
    maxHeight: 300,
    borderRadius: 4,
    marginBottom: 20,
  },
  title: {
    fontSize: 20,
    color: '#fff',
    fontWeight: "500",
  },
  plot:{
    fontSize: 14,
    color: '#fff',
    marginBottom: 5

  }
});
export default SavedScreen;
