import React from "react";
import {
  View,
  Text,
  FlatList,
  StyleSheet
} from "react-native";
import ResultsDetail from "./ResultsDetails";

const ResultsList = ({navigation, results, Title}) => {
  if(!results.length) {
    return null
}

  return (
    <View>
      <Text style={styles.title}>{Title}</Text>
      <View ><FlatList
      // showsHorizontalScrollIndicator={false}
      // horizontal
        data={results}
        keyExtractor={(movies) => movies.imdbID}
        renderItem={({ item }) => {
          return (
            
              <ResultsDetail result={item} />
            

          );
        }}
        style={styles.container}/></View>
    </View>
  );
};
const styles = StyleSheet.create({
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 5
  },
  container:{
    flexDirection: 'row',
    justifyContent: 'space-between',
    // flexWrap: 'wrap',
    // alignItems: 'stretch'
  }, 
  // resultDetail:{
  //   flexDirection: "column"
  // }
});
export default ResultsList;
