import React from 'react';
import { View, Text, StyleSheet , Modal, SafeAreaView, Button} from 'react-native';

const FilterOpt = ({ modalVisible, setModalVisible }) => {
    return (
        <Modal animationType="slide"
        transparent={false}
        visible={modalVisible}
        style={{ flex: 1 }}
        onRequestClose={() => {
          Alert.alert('Modal has been closed.');
          setModalVisible(!modalVisible);
        }}>
            <SafeAreaView style={{ flex: 1, padding: 20 }}>
        <View style={{ marginBottom: 20, paddingHorizontal: 30 }}>
          <Text style={{ fontSize: 26 }}>Filtering Fruits</Text>
        </View>

        <Button title="Clear" onPress={() => {
          setModalVisible(!modalVisible)
        }} />
        </SafeAreaView>
        </Modal>
        
    )
}

const styles = StyleSheet.create({

})

export default FilterOpt;
