import React, {useState} from 'react';
import {
  View,
  StyleSheet,
  TouchableOpacity,
  ImageBackground,
} from 'react-native';
import * as Animatable from 'react-native-animatable' 
import Icon from 'react-native-vector-icons/AntDesign'
import { withNavigation } from 'react-navigation';
const AnimatedIcon = Animatable.createAnimatableComponent(Icon)
  const colors = {
  transparent: 'transparent',
  white: '#fff',
  heartColor: '#e92f3c',
  textPrimary: '#fff',
  black: '#000', 
}
const ResultsDetail = ({ result, navigation }) => {
  const [liked, setLiked] = useState(false);
  
  return (
    <ImageBackground source={{ uri: result.Poster }} style={styles.cardbg}>
    <TouchableOpacity
        onPress={(id) =>{
          setLiked(!liked);
          navigation.navigate('Show', { id: result.imdbID })
        } }>
      <AnimatedIcon
              name={liked ? 'heart' : 'hearto'}
              color={liked ? colors.heartColor : colors.textPrimary}
              size={18}
              style={styles.icon}
            />
    </TouchableOpacity>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  image: {
    width: 200,
    height: 300,
    borderRadius: 4,
  },
  card: {
    width:200,
    paddingLeft: 5,
    paddingRight:5,
    backgroundColor: '#fff',
    flexDirection: 'column',
    marginLeft: 5,
  },
  cardbg: {
    width:100,
    height:300,
    paddingLeft: 5,
    paddingRight:5,
    backgroundColor: '#fff',
    flexDirection: 'column',
    marginLeft: 5,
  },
  imageSection: {
    alignItems: 'center',
    flex: 2,
  },
  contentSection: {
    flex: 2,
    paddingLeft: 2,
  },
  title: {
    fontSize: 14,
  },
  icon: {
    fontSize: 20,
    textAlign: 'right',
  },
    animatedIcon: {
    position: 'absolute',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 2,
    borderRadius: 160,
    opacity: 0
  }
});

export default withNavigation(ResultsDetail);
