import React from 'react';
import { View, Text, StyleSheet, TextInput } from 'react-native';
import {Feather} from '@expo/vector-icons'

const searchBar = () => {
    return (
        <View>
            Searchbar
        </View>
    )
}
const styles = StyleSheet.create({
    backgroundStyle:{
        marginTop:10,
        backgroundColor: '#F0EEEE',
        height: 50,
        borderRadius: 5,
        marginHorizontal: 15,
        flexDirection: 'row',
        marginBottom: 10
    },
    inputStyle:{
        flex: 1,
        fontSize: 18
    },
    iconStyle:{
        fontSize: 35,
        alignSelf:'center',
        marginHorizontal: 15
    }
})
export default searchBar;
