import axios from "axios";
import React, { useState, useEffect, useContext} from "react";
import { View, Text, StyleSheet, Image, ImageBackground, FlatList } from "react-native";
import { SafeAreaView } from "react-navigation";
import SavedMovieForm from "../components/SavedMovieForm";
import { Context } from '../context/movieContext';
const url = "http://www.omdbapi.com/?apikey=a091d52e";


const SavedScreen = () => {
  const [result, setResult] = useState(null);
  const { state } = useContext(Context);

  useEffect(() => {
    // getResult(id);
  }, []);
  if (!state) {
    return null;
  }

  return (
    <SafeAreaView>
      <ImageBackground
        source={{ uri: 'https://pixy.org/src/5/54116.jpg' }}
        blurRadius={3}
        resizeMode="cover">
        <View style={styles.background}>
        <FlatList 
              data= {state}
              keyExtrator = {state => state.id}
              renderItem = {( { item }) =>{
                return (
                  <Text>{item.content.Title}</Text>
                )
              }}
            />

        </View>
      </ImageBackground>
      </SafeAreaView>
  );
};
const styles = StyleSheet.create({
  background: {
    color: '#fff',
    height: '100%'
  },
  image: {
    marginTop: 15,
    minHeight: 200,
    borderRadius: 4,
    marginBottom: 10,
  },
  title: {
    fontSize: 20,
    color: '#fff',
    fontWeight: "500",
  },
  plot:{
    fontSize: 14,
    color: '#fff',
    marginBottom: 5

  }
});
export default SavedScreen;
