import React, { useState, useContext, useEffect } from "react";
import { View, StyleSheet, Text } from "react-native";
import SearchBar from "../components/SearchBar";
import ResultsList from "../components/ResultsList";
import { withNavigation } from "react-navigation";
import useResults from "../hooks/useResults";
import FilterOpt from "../components/FilterOpt";
import { Feather } from "@expo/vector-icons";
//import data structures
import { initialFilter } from "../data/initialFilter";
import { Context } from "../context/movieContext";
import { Button } from "@rneui/themed";
import {searchApi} from "../api/omdb";
const IndexScreen = ({ navigation }) => {
  const [term, setTerm] = useState("Batman");
  // const [searchApi, results, errorMessage] = useResults();
  const [modalVisible, setModalVisible] = useState(false);
  const [filter, setFilter] = useState(initialFilter);
  const [intermediateFilter, setIntermediateFilter] = useState(initialFilter);
  const { state, getMovies } = useContext(Context);
  const [results, setResults] = useState();
  const [type, setType] = useState("movie")

  function init(initialCount) {
    return setResults(searchApi(term));
  }

  useEffect(() => {
    
    getMovies();
    //fetch again when back again
    const listner = navigation.addListener("didFocus", () => {
      getMovies();
    });

    return () => {
      listner.remove();
    };
  }, []);

  const changeType = (type) => {
    setType(type)
  }
  console.log("results search: "+results);
  // const filterResultsByType = (Type) => {
  //   return results.filter((result) => {
  //     return result.Type === Type;
  //   });
  // };

  //creating the year filter
  // const yearResult = (results) =>{
  //   results.map((item) => item.Year)
  //   .filter((value, index, self) => self.indexOf(value) === index);
  // }
    

  return (
    <>
      <View style={styles.backgroundStyle}>
        <SearchBar
          style={styles.search}
          term={term}
          onTermChange={setTerm}
          onTermSubmit={() => searchApi(term)}
        />
        <Feather
          name="sliders"
          style={styles.iconStyle}
          onPress={() => setModalVisible(!modalVisible)}
        />
      </View>
      {/* {errorMessage ? <Text>{errorMessage}</Text> : null} */}
      <View style={{flexDirection: 'row'}}>
              <Button title="Movies"
      buttonStyle={{
                backgroundColor: 'rgba(78, 116, 289, 1)',
                borderRadius: 3,
              }}
              containerStyle={{
                width: 100,
                marginHorizontal: 10,
                marginVertical: 10,
              }}
              onPress={() => changeType("movie")}
              />
                    <Button title="Series"
      buttonStyle={{
                backgroundColor: 'rgba(78, 116, 289, 1)',
                borderRadius: 3,
              }}
              containerStyle={{
                width: 100,
                marginHorizontal: 10,
                marginVertical: 10,
              }}
              onPress={() => changeType("series")}/>
              <Button title="episode"
      buttonStyle={{
                backgroundColor: 'rgba(78, 116, 289, 1)',
                borderRadius: 3,
              }}
              containerStyle={{
                width: 100,
                marginHorizontal: 10,
                marginVertical: 10,
              }}
              onPress={() => changeType("episode")}/>
      </View>
      <ResultsList results={results} searchTerm={term} filter={filter} saved={state}/>
      {/* <ResultsList results={filterResultsByType(type)} searchTerm={term} filter={filter} saved={state}/> */}

      {/* <FilterOpt
        modalVisible={modalVisible}
        setModalVisible={setModalVisible}
        filter={filter}
        setFilter={setFilter}
        intermediateFilter={intermediateFilter}
        setIntermediateFilter={setIntermediateFilter}
        yearResult={yearResult}
      /> */}
      <Button
        title="View Saved Movies"
        onPress={() => navigation.navigate("Saved")}
      />
    </>
  );
};

const styles = StyleSheet.create({
  backgroundStyle: {
    marginTop: 10,
    backgroundColor: "#F0EEEE",
    height: 50,
    borderRadius: 5,
    marginHorizontal: 15,
    flexDirection: "row",
    minWidth: 50,
    marginBottom: 10,
  },
  resultList: {
    marginBottom: 10,
  },
  iconStyle: {
    fontSize: 20,
    alignSelf: "center",
    marginHorizontal: 15,
  },
  buttonStyle:{
    backgroundColor: 'rgba(78, 116, 289, 1)',
    borderRadius: 3,
  },
  containerStyle:{
    width: 200,
    marginHorizontal: 50,
    marginVertical: 10,
  }
});

export default withNavigation(IndexScreen);
