import React, { useState, useEffect, useContext } from "react";
import {
  View,
  Text,
  StyleSheet,
  Image,
  ImageBackground,
  FlatList,
  TouchableOpacity,
  Button,
  SafeAreaView,
  ScrollView
} from "react-native";
// import { SafeAreaView } from "react-navigation";
import { Context } from "../context/movieContext";
import { Card } from "@rneui/themed";

const SavedScreen = ({ navigation }) => {
  const { state, deleteSavedMovie } = useContext(Context);
  const _renderItem = ({item}) => (
    <Card containerStyle={styles.cardbg} wrapperStyle={{}} key={item.id}>
    {/* <View style={styles.cardbg}  > */}
    <Card.Title>{item.Title}</Card.Title>
    {/* <Text style={styles.title}>{item.Title}</Text> */}
    <Image source={{ uri: item.Poster }} style={styles.image} />
    <Text style={styles.text}>Movie plot: {item.Plot}</Text>
    <Text style={styles.text}>Your Note: {item.description}</Text>
    <Button
      title="Edit Movie"
      style={styles.btn}
      onPress={() => navigation.navigate("Edit", { id: item.id })}
    />
    <Button
      title="Delete from list"
      style={styles.btn}
      onPress={() => deleteSavedMovie(item.id)}
    />
  </Card>
  );
  return (
    <SafeAreaView>
      <ImageBackground
        source={{ uri: "https://wallpaperaccess.com/full/2677518.jpg" }}
        blurRadius={5}
        resizeMode="cover"
      >
        <ScrollView style={styles.background}>
          <FlatList
            horizontal
            data={state}
            keyExtractor={(item) => item.id}
            renderItem={_renderItem}
          />
        </ScrollView>
      </ImageBackground>
    </SafeAreaView>
  );
};
const styles = StyleSheet.create({
  background: {
    color: "#fff",
    height: "100%",
    alignContent: 'center', 
    
  },
  cardbg: {
    width: 325,
    height: 600,
    flexDirection: "column",
    backgroundColor: "purple",
  },

  title: {
    fontSize: 24,
    color: "white",
    fontWeight: "bold",
    marginBottom: 5,
    marginTop: 1,
  },
  image: {
    width: "100%",
    height: 250,
    resizeMode: "contain",
  },
  text: {
    fontSize: 18,
    color: "white",
    textAlign: "justify",
    marginTop: 10,
  },
  btn: {
    marginTop: 10,
  },
});
export default SavedScreen;
