import React, { useState, useEffect, useContext } from "react";
import {
  View,
  Text,
  StyleSheet,
  Image,
  ImageBackground,
  FlatList,
  TouchableOpacity,
  Button,
  SafeAreaView
} from "react-native";
// import { SafeAreaView } from "react-navigation";
import { Context } from "../context/movieContext";
const url = "http://www.omdbapi.com/?apikey=a091d52e";

const SavedScreen = ({ navigation }) => {
  const { state, deleteSavedMovie, getMovies } = useContext(Context);

  // useEffect(() => {
  //   getMovies();
  //   //fetch again when back again
  //   const listner = navigation.addListener("didFocus", () => {
  //     getMovies();
  //   });

  //   return () => {
  //     listner.remove();
  //   };
  // }, []);
  if (!state) {
    return null;
  }
  const _keyExtractor = (item, index) => index.toString();
  const renderItem = ({ item }) => {
    <SafeAreaView style={styles.cardbg} >
      <Text style={styles.title}>{item.content.Title}</Text>
      <Image
        source={{ uri: item.content.Poster }}
        style={styles.image}
      />
      <Text style={styles.text}>
        Movie plot: {item.content.Plot}
      </Text>
      <Text style={styles.text}>Your Note: {item.description}</Text>
      <Button
        title="Edit Movie"
        style={styles.btn}
        onPress={() => navigation.navigate("Edit", { id: item.id })}
      />
      <Button
        title="Delete from list"
        style={styles.btn}
        onPress={() => deleteSavedMovie(item.id)}
      />
    </SafeAreaView>
  }
  return (
    <SafeAreaView>
      <ImageBackground
        source={{ uri: "https://wallpaperaccess.com/full/2677518.jpg" }}
        blurRadius={5}
        resizeMode="cover"
      >
        <View style={styles.background}>
          <FlatList
            horizontal
            data={state}
            keyExtractor={_keyExtractor}
            renderItem={renderItem} />
        </View>
      </ImageBackground>
    </SafeAreaView>
  );
};
const styles = StyleSheet.create({
  background: {
    color: "#fff",
    height: "100%",
  },
  cardbg: {
    width: 330,
    height: 600,
    padding: 15,
    margin: 15,
    flexDirection: "column",
    backgroundColor: "purple",
  },

  title: {
    fontSize: 24,
    color: "white",
    fontWeight: "bold",
    marginBottom: 5,
    marginTop: 1,
  },
  image: {
    width: "100%",
    height: 250,
    resizeMode: "contain",
  },
  text: {
    fontSize: 18,
    color: "white",
    textAlign: "justify",
    marginTop: 10,
  },
  btn: {
    marginTop: 10,
  },
});
export default SavedScreen;
