import React, {useState} from 'react';
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  Image,
  TouchableOpacity,
} from 'react-native';
import { Feather } from '@expo/vector-icons';
import * as Animatable from 'react-native-animatable' 
import Icon from 'react-native-vector-icons/AntDesign'
import { withNavigation } from 'react-navigation';
const AnimatedIcon = Animatable.createAnimatableComponent(Icon)
  const colors = {
  transparent: 'transparent',
  white: '#fff',
  heartColor: '#e92f3c',
  textPrimary: '#515151',
  black: '#000', 
}
const ResultsDetail = ({ result, navigation }) => {
  const [liked, setLiked] = useState(false)

  return (
    <View style={styles.card}>
      <TouchableOpacity
        onPress={() => navigation.navigate('Show', { id: result.imdbID })}>
        <View style={styles.imageSection}>
          <Image style={styles.image} source={{ uri: result.Poster }} />
        </View>
        <View style={styles.contentSection}>
          <Text style={styles.title}>{result.Title}</Text>
          <Text>Year Released: {result.Year}</Text>
          <Text>Type: {result.Type}</Text>
        </View>
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => setLiked(!liked)}>
      <AnimatedIcon
              name={liked ? 'heart' : 'hearto'}
              color={liked ? colors.heartColor : colors.textPrimary}
              size={18}
              style={styles.icon}
            />
    </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  image: {
    width: '100%',
    height: 150,
    borderRadius: 4,
    marginBottom: 5,
  },
  card: {
    padding: 15,
    backgroundColor: '#F0EEEE',
    flexDirection: 'column',
    width: '100%',
    marginBottom: 5,
  },
  imageSection: {
    flex: 2,
  },
  contentSection: {
    flex: 2,
    paddingLeft: 2,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  icon: {
    fontSize: 20,
    textAlign: 'right'
  },
    animatedIcon: {
    position: 'absolute',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 2,
    borderRadius: 160,
    opacity: 0
  }
});

export default withNavigation(ResultsDetail);
