import axios from "axios";
import React, { useState, useEffect, useContext} from "react";
import { View, Text, StyleSheet, Image, ImageBackground, FlatList, TouchableOpacity } from "react-native";
import { SafeAreaView } from "react-navigation";
import SavedMovieForm from "../components/SavedMovieForm";
import { Context } from '../context/movieContext';
const url = "http://www.omdbapi.com/?apikey=a091d52e";
import ResultsDetails from "../components/ResultsDetails";

const SavedScreen = () => {
  const [result, setResult] = useState(null);
  const { state } = useContext(Context);

  useEffect(() => {
    // getResult(id);
  }, []);
  if (!state) {
    return null;
  }

  return (
    <SafeAreaView>
      <ImageBackground
        source={{ uri: 'https://wallpaperaccess.com/full/2677518.jpg' }}
        blurRadius={5}
        resizeMode="cover">
        <View style={styles.background}>
          <Text style={styles.title}>Saved Movies</Text>
        <FlatList 
              horizontal
              data= {state}
              keyExtrator = {state => state.id}
              renderItem = {( { item }) =>{
                return (
                  <ImageBackground source={{ uri: item.content.Poster }} style={styles.cardbg}>
                  </ImageBackground>
                )
              }}
            />

        </View>
      </ImageBackground>
      </SafeAreaView>
  );
};
const styles = StyleSheet.create({
  background: {
    color: '#fff',
    height: '100%',
  },
  cardbg: {
    width:350,
    height:450,
    margin: 15,
    padding: 15,
    shadowColor: 'white',
    shadowOpacity: 1,
    shadowOffset: -1,
    flexDirection: 'column',
  },
  title: {
    fontSize: 24,
    color: 'white',
    fontWeight: 'bold',
    marginBottom: 5
  },
});
export default SavedScreen;
