import React, { useState, useContext, useEffect } from "react";
import {
  Button,
  ImageBackground,
  StyleSheet,
  View,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import SearchBar from "../components/SearchBar";
import ResultsList from "../components/ResultsList";
import { withNavigation } from "react-navigation";
import { Context } from "../context/movieContext";
import { fetchMovies } from "../api/omdb";



const IndexScreen = ({ navigation }) => {
  const [term, setTerm] = useState("");
  const [searchMovie, setSearchMovie] = useState("prison break");
  // const { state, getMovies } = useContext(Context); //Pass value from context to index


  useEffect(() => {
    setMovies(fetchMovies(searchMovie));
    setSearchMovie("");

    //fetch again when back again
    const listner = navigation.addListener("didFocus", () => {
      setMovies(fetchMovies(searchMovie));
    });

    return () => {
      listner.remove();
    };
  }, []);

  const filterResultsByType = (Type) => {
    return setMovies.filter((state) => {
      return state.Type === Type;
    });
  };

  return (
    <>
        <SearchBar style={styles.search}
          term={term}
          onTermChange={setTerm}
          onTermSubmit={() => getMovies(term)}
        />

      <ScrollView>
        <ResultsList
          results={filterResultsByType("movie")}
          Title="Movies"
          style={styles.resultList}
        />
        <ResultsList
          results={filterResultsByType("series")}
          Title="Series"
          style={styles.resultList}
        />
        <ResultsList
          results={filterResultsByType("episode")}
          Title="Series"
          style={styles.resultList}
        />
      </ScrollView>

      <Button style={styles.button}
        title="View Saved Movies"
        onPress={() => navigation.navigate("Saved")}
      />
    </>
  );
};

const styles = StyleSheet.create({
  resultList: {
    marginBottom: 10,
  }, 
button:{
  height:150,
  minWidth: 50,
  fontSize:50
}
});

export default withNavigation(IndexScreen);
