import React, { useState } from "react";
import {
  View,
  StyleSheet,
  TouchableOpacity,
  ImageBackground,
} from "react-native";
import * as Animatable from "react-native-animatable";
import Icon from "react-native-vector-icons/AntDesign";
import { withNavigation } from "react-navigation";
const AnimatedIcon = Animatable.createAnimatableComponent(Icon);
const colors = {
  transparent: "transparent",
  white: "#fff",
  heartColor: "#e92f3c",
  textPrimary: "#fff",
  black: "#000",
};
const ResultsDetail = ({ result, navigation }) => {
  const [liked, setLiked] = useState(false);

  return (
    <View style={styles.container}>
    <ImageBackground source={{ uri: result.Poster }} style={styles.cardbg}>
      <TouchableOpacity
        onPress={(id) => {
          setLiked(!liked);
          navigation.navigate("Show", { id: result.imdbID });
        }}
      >
        <AnimatedIcon
          name={liked ? "heart" : "hearto"}
          color={liked ? colors.heartColor : colors.textPrimary}
          size={18}
          style={styles.icon}
        />
      </TouchableOpacity>
    </ImageBackground>
    </View>
  );
};

const styles = StyleSheet.create({
  container:{
    // justifyContent: 'space-between',
    // flexWrap: 'wrap',
    // alignItems: 'stretch'   
    //  margin: 5,
    // padding: 5
    marginHorizontal: 'auto'
  },
  cardbg: {
    width: 180,
    height: 280,
    padding: 2,
    margin: 2,
    position: 'relative'
  },
  icon: {
    fontSize: 20,
    textAlign: "right",
    padding: 5
  },
  animatedIcon: {
    position: "absolute",
    justifyContent: "center",
    alignItems: "center",
    zIndex: 2,
    borderRadius: 160,
    opacity: 0,
  },
});

export default withNavigation(ResultsDetail);
