import React, { useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  Modal,
  SafeAreaView,
  Button,
  FlatList,
} from "react-native";
// import { CheckBox } from 'react-native-elements'
import { ListItem, CheckBox } from "@rneui/themed";

const FilterOpt = ({
  modalVisible,
  setModalVisible,
  yearResult,
  typeResult,
}) => {
  const [checked, setChecked] = React.useState(false);
  const [check1, setCheck1] = useState(true);


  const renderItem = ({ yearResult }) => (
    <ListItem bottomDivider>
      <ListItem.Content>
        <CheckBox
          center
          title={(yearResult)}
          checked={check1}
          onPress={() => setCheck1(!check1)}
        />
      </ListItem.Content>
    </ListItem>
  );
  return (
    <Modal
      animationType="slide"
      transparent={false}
      visible={modalVisible}
      style={{ flex: 1 }}
      onRequestClose={() => {
        Alert.alert("Modal has been closed.");
        setModalVisible(!modalVisible);
      }}
    >
      <SafeAreaView style={{ flex: 1, padding: 20 }}>
        <View style={{ marginBottom: 20, paddingHorizontal: 30 }}>
          <Text style={{ fontSize: 26 }}>Filters:</Text>
          <Text style={[styles.label, { marginTop: 20 }]}>Year</Text>
          <CheckBox
            center
            title="Click Here"
            checked={check1}
            onPress={() => setCheck1(!check1)}
          />
          <FlatList
            // keyExtractor={(movies) => movies.imdbID}
            data={yearResult}
            renderItem={renderItem}
          />
          <Text style={[styles.label, { marginTop: 20 }]}>Type</Text>
          <FlatList
            data={typeResult}
            horizontal
            // keyExtractor={(movies) => movies.imdbID}
            renderItem={({ item }) => {
              return <Text>{item}</Text>;
            }}
          />
        </View>

        <Button
          title="Clear"
          onPress={() => {
            setModalVisible(!modalVisible);
          }}
        />
      </SafeAreaView>
    </Modal>
  );
};

const styles = StyleSheet.create({
  label: {
    fontSize: 15,
    fontWeight: "bold",
  },
});

export default FilterOpt;
