import React, {useContext, useEffect, useState} from "react";
import { View, Text, FlatList, StyleSheet } from "react-native";
import ResultsDetail from "./ResultsDetails";
import { Context } from "../context/movieContext";
const ResultsList = ({ navigation, results, searchTerm,  filter }) => {
  const {state} = useContext(Context)
  const [like, setLiked] = useState([])

  if (!results) {
    return null;
  }
console.log(state);
  const liked = (imdbID) =>{
    return state.find(item => {
           return item.content.imdbID === imdbID;
        });
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Search result: {searchTerm}</Text>
      <FlatList
        data={results}
        style={styles.row}
        numColumns= {2}
        keyExtractor={(movies) => movies.imdbID}
        renderItem={({ item }) => {

          return (
              <ResultsDetail result={item} liked={liked(item.imdbID)} />
          );
        }}
      /> 
    </View>
  );
};
const styles = StyleSheet.create({
  title: {
    fontSize: 14,
    marginBottom: 5,
    marginLeft: 5
  },
  container: {
    flex: 2,
    width: '100%',
  },
  row:{
    width: '100%', 
    // alignContent: 'space-between'
  }
});
export default ResultsList;
