import React, { useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  Modal,
  SafeAreaView,
  Button,
  FlatList,
} from "react-native";
// import { CheckBox } from 'react-native-elements'
import { ListItem, CheckBox } from "@rneui/themed";

const FilterOpt = ({
  modalVisible,
  setModalVisible,
  yearResult,
  typeResult,
}) => {
  const [checked, setChecked] = React.useState(false);
  const [check1, setCheck1] = useState(false);

  const changeSchool = (schoolId) => {
    if (!checked) {
      // add schoolId to user.schools
      console.log("add to list");
    } else {
      // remove schoolId from user.schools
      console.log("remove from list");
    }
  };

  const onChangeValue = (item, index, newValue) => {
    const newData = data.map(newItem => {
      if (newItem.id == item.id) {
        return {
          ...newItem,
          selected: newValue,
        }
      }
      return newItem
    })
    setData(newData);
  }
  return (
    <Modal
      animationType="slide"
      transparent={false}
      visible={modalVisible}
      style={{ flex: 1 }}
      onRequestClose={() => {
        Alert.alert("Modal has been closed.");
        setModalVisible(!modalVisible);
      }}
    >
      <SafeAreaView style={{ flex: 1, padding: 20 }}>
        <View style={{ marginBottom: 20, paddingHorizontal: 30 }}>
          <Text style={{ fontSize: 26 }}>Filters:</Text>
          <Text style={[styles.label, { marginTop: 20 }]}>Year</Text>
          {/* <FlatList
            data={yearResult}
            keyExtractor={(year) => year.i}
            renderItem={({ item }) => {
              return (
                <CheckBox
                key={item.i}
                center
                title={item}
                checked={check1}
                onValueChange={newValue => onChangeValue(item, item.i, newValue)}
                containerStyle={{ padding: 3 }}
              />
              );
            }}
          /> */}

          {/* {yearResult.map((item, i) => {
            const [check, setCheck1] = useState(false);
            <ListItem
              key={i}
              containerStyle={{ padding: 3 }}
              title={item}
              accessory={() => renderAccessory(item)}
            >
              <ListItem.Content>
                <CheckBox
                  center
                  title={item}
                  checked={check1}
                  onValueChange={newValue => onChangeValue(item, i, newValue)}
                  containerStyle={{ padding: 3 }}
                />
              </ListItem.Content>
            </ListItem>
})} */}

          <Text style={[styles.label, { marginTop: 20 }]}>Type</Text>
          {/* <FlatList
            data={typeResult}
            keyExtractor={keyExtractor}
            renderItem={renderItem}
          /> */}
        </View>
        <Button
          title="Apply"
          onPress={() => {
            setModalVisible(!modalVisible);
          }}
        />
        <Button
          title="Clear"
          onPress={() => {
            setModalVisible(!modalVisible);
          }}
        />
      </SafeAreaView>
    </Modal>
  );
};

const styles = StyleSheet.create({
  label: {
    fontSize: 15,
    fontWeight: "bold",
  },
});

export default FilterOpt;
